import React ,{ Component } from 'react';

class Forms extends Component{
    constructor (){
        super()
        this.state = {
            firstName:"",
            lastName:"",
            email:"",
            password:"",
            checked:true,
        }
        this.handleChange = this.handleChange.bind(this)
    }
    handleChange(event){
        const {name,value,type,checked} = event.target
        this.setState({ 
            [name] : value 
        })
    }
    render(){
        return(
            <div>
                <form>
                    <input 
                        type="text" 
                        name="firstName" 
                        placeholder="First Name" 
                        onChange={this.handleChange}
                    />
                    <input type="email" name="lastName"  placeholder="Last Name" onChange={this.handleChange}/>
                    <input type="password"  name="password"/>
                    <textarea />
                    <input type="checkbox" />
                    <input type="radio" />
                    <select>
                        <option></option>
                        <option></option>
                        <option></option>
                    </select>    
                </form>
            </div>
        )
    }
}
export default Forms
