class App extends React.Component{

  constructor(){
    super()
    this.state ={
      todo:todosData
    }
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(id){
    console.log("changed",id)
  }

  render(){
    const todoItems = this.state.todo.map((item)=>(<TodoItem  key={item.id} todo={item} handleChange={this.handleChange}/>))

    return (
      <div className="todo-list">
        {todoItems}
      </div>
    )
  }
}

  export default App;
